engine.trackerLib['hdclub'] = {
    id: 'hdclub',
    title: 'HDclub',
    icon: 'data:image/x-icon;base64,%3D',
    desc: 'Трекер HDClub - скачать бесплатно фильмы HD, скачать Blu-ray фильмы, HD DVD и HD audio, HDTV торрент.',
    flags: {
        auth: 1,
        language: 'ru',
        cyrillic: 1,
        allowProxy: 1
    },
    categoryList: {
        serials: [],
        music: ['HD Audio', 'Music Video'],
        games: [],
        films: ['Movie'],
        cartoon: ['Animation'],
        books: [],
        soft: [],
        anime: [],
        doc: ['Documentary', 'TV Show'],
        sport: ['Sport'],
        xxx: [],
        humor: []
    },
    search: {
        searchUrl: 'http://hdclub.org/browse.php',
        baseUrl: 'http://hdclub.org/',
        requestType: 'GET',
        requestData: 'search=%search%',
        requestMimeType: 'text/html; charset=windows-1251',
        onGetRequest: function (details) {
            "use strict";
            details.query = exKit.funcList.encodeCp1251(details.query);
        },
        listItemSelector: '#highlighted>tr',
        torrentSelector: {
            categoryTitle: {selector: 'td:eq(0)>a img', attr: 'alt'},
            categoryUrl: {selector: 'td:eq(0)>a', attr: 'href'},
            categoryId: {selector: 'td:eq(0)>a img', attr: 'alt'},
            title: 'td:eq(2)>table>tbody>tr>td:eq(0)>a',
            url: {selector: 'td:eq(2)>table>tbody>tr>td:eq(0)>a', attr: 'href'},
            size: {selector: 'td:eq(8)>nobr', childNodeIndex: 0},
            seed: 'td:eq(6)',
            peer: 'td:eq(7)',
            date: '#cleft font'
        },
        onGetValue: {
            categoryId: function (details, value) {
                "use strict";
                return exKit.funcList.idInCategoryList(details.tracker, value);
            },
            size: function (details, value) {
                "use strict";
                return exKit.funcList.sizeFormat(value)
            },
            date: function (details, value) {
                "use strict";
                return exKit.funcList.dateFormat(0, value)
            }
        }
    }
};